CREATE TABLE `immobilier`.`logement` ( `id_logement` INT(3) NOT NULL AUTO_INCREMENT , `titre` VARCHAR(50) NOT NULL , `adresse` VARCHAR(50) NOT NULL , `ville` VARCHAR(50) NOT NULL , `cp` INT(5) UNSIGNED ZEROFILL NOT NULL , `surface` INT(5) NOT NULL , `prix` INT(10) NOT NULL , `photo` VARCHAR(500) NOT NULL , `type` ENUM("location","vente") NOT NULL , `description` TEXT NOT NULL , PRIMARY KEY (`id_logement`)) ENGINE = InnoDB;
